/**********************************************************************************************************************************************************************
The purpose of this scripts is to create and execute the update table between stage and dw. 
***********************************************************************************************************************************************************************/


CREATE PROCEDURE [meta].[MaintainDWUpdateTable]

@Table NVARCHAR(100),--Input is the table name without schema
@DestinationSchema NVARCHAR(10),--Input is the destination schema (dim, fact or bridge)
@PrintSQL BIT--Enter 1 if you want to print the dynamic SQL and 0 if you want to execute the dynamic SQL

AS

SET NOCOUNT ON
/********************************************************************************************************************************************************************** Support variables ***********************************************************************************************************************************************************************/ DECLARE @CRLF NVARCHAR(2) = CHAR(13) + CHAR(10) DECLARE @DatabaseNameExtract NVARCHAR(128) = (SELECT VariableValue FROM meta.Variables WHERE VariableName = 'DatabaseNameExtract') DECLARE @DefaultMaxDop NVARCHAR(3) = (SELECT VariableValue FROM meta.Variables WHERE VariableName = 'DefaultMaxDop')
/********************************************************************************************************************************************************************** 1. Update FrameworkMetaData table with IDs ***********************************************************************************************************************************************************************/ INSERT INTO meta.FrameworkMetaData (SourceObjectID)
SELECT SourceObjects.ID FROM meta.SourceObjects LEFT JOIN meta.FrameworkMetaData ON FrameworkMetaData.SourceObjectID = SourceObjects.ID WHERE FrameworkMetaData.BusinessMatrixID IS NULL AND FrameworkMetaData.SourceObjectID IS NULL AND FrameworkMetaData.TargetObjectID IS NULL AND SourceObjects.ID = @SourceObjectID
DELETE FROM meta.FrameworkMetaData WHERE SourceObjectID NOT IN (SELECT ID FROM meta.SourceObjects) AND SourceObjectID IS NOT NULL;
/********************************************************************************************************************************************************************** 2. Create and insert data into table variables **********************************************************************************************************************************************************************/ DECLARE @InformationSchemaTables TABLE (SourceObjectID INT, TableName NVARCHAR(128), ObjectName NVARCHAR(128),OrdinalPosition INT, ParallelizationFlag BIT, PartitionFlag BIT, UseModulusFlag BIT) INSERT @InformationSchemaTables SELECT SourceObjectID, [Name], [ObjectName], ROW_NUMBER() OVER ( ORDER BY ObjectName), ParallelizationFlag, PartitionFlag, UseModulusFlag FROM meta.SourceObjectDefinitions WHERE SourceObjectID = @SourceObjectID GROUP BY SourceObjectID,Name,ObjectName, ParallelizationFlag, PartitionFlag, UseModulusFlag
/********************************************************************************************************************************************************************** 3. Create Loop counter variables **********************************************************************************************************************************************************************/ DECLARE @OuterCounter INT = 1 DECLARE @MaxTable INT = (SELECT MAX(OrdinalPosition) FROM @InformationSchemaTables)
/********************************************************************************************************************************************************************** 4. Create outer loop **********************************************************************************************************************************************************************/ DECLARE @Parallelization BIT DECLARE @PartitionFlag BIT DECLARE @UseModulusFlag BIT
WHILE @OuterCounter <= @MaxTable
BEGIN
SELECT @PartitionFlag = PartitionFlag ,@SourceObjectID = SourceObjectID ,@UseModulusFlag = UseModulusFlag FROM @InformationSchemaTables WHERE @OuterCounter = OrdinalPosition
/********************************************************************************************************************************************************************** 5. Create and insert data into table variables **********************************************************************************************************************************************************************/ DECLARE @InformationSchema TABLE (TableName NVARCHAR(128), PartitionDefinition NVARCHAR(max),PartitionLowerBound NVARCHAR(100), PartitionUpperBound NVARCHAR(100), OrdinalPosition INT)
INSERT @InformationSchema EXEC(' SELECT [Name] , REPLACE(SourceObjectPartition.PartitionValueColumnDefinition,'''''''','''''''''''') , PartitionLowerBound , PartitionUpperBound , ROW_NUMBER() OVER ( ORDER BY ObjectName) FROM meta.SourceObjectDefinitions INNER JOIN meta.SourceObjectPartition ON SourceObjectPartition.SourceObjectID = SourceObjectDefinitions.SourceObjectID WHERE PartitionFlag = 1 AND SourceObjectDefinitions.SourceObjectID = ' + @SourceObjectID)
/********************************************************************************************************************************************************************** 6. Create Loop counter variables **********************************************************************************************************************************************************************/ DECLARE @Counter INT DECLARE @MaxPartitions INT
SELECT @Counter = 1 ,@MaxPartitions = (SELECT MAX(OrdinalPosition) FROM @InformationSchema)
/********************************************************************************************************************************************************************** 7. Create loop **********************************************************************************************************************************************************************/ DECLARE @SQL NVARCHAR(MAX) DECLARE @PlaceholderSQL NVARCHAR(MAX) DECLARE @DefaultPartitionSQL NVARCHAR(MAX) = 'WITH Dataset AS (SELECT ROW_NUMBER() OVER ( ORDER BY object_id) AS RowN FROM sys.all_objects) SELECT 0 AS RowN, ''Dummy'' AS PartitionDefinition, 0 AS PartitionLowerBound, 0 AS PartitionUpperBound UNION ALL SELECT RowN , ''Dummy'' AS PartitionDefinition, RowN AS PartitionLowerBound, RowN AS PartitionUpperBound FROM Dataset WHERE RowN < (SELECT VariableValue FROM meta.Variables WHERE VariableName = ''DefaultMaxDop'')'
WHILE @Counter <= @MaxPartitions
BEGIN
SELECT @PlaceholderSQL = 'SELECT ' + CAST(OrdinalPosition AS NVARCHAR(20)) + ' AS RowN, ''' + PartitionDefinition + ''' AS PartitionDefinition, ' + PartitionLowerBound + ' AS PartitionLowerBound , ' + PartitionUpperBound + ' AS PartitionUpperBound ' + IIF(@Counter = @MaxPartitions,'',' UNION ALL') + @CRLF FROM @InformationSchema WHERE OrdinalPosition = @Counter
SET @SQL = CONCAT(@SQL,@PlaceholderSQL)
SET @PlaceholderSQL = ''
SET @Counter = @Counter + 1
END
/********************************************************************************************************************************************************************** 8. Update FrameworkMetaDate **********************************************************************************************************************************************************************/ IF @PrintSQL = 0 BEGIN IF @PartitionFlag = 1 AND @UseModulusFlag = 0 BEGIN UPDATE meta.FrameworkMetaData SET PartitionSQLScript = @SQL WHERE SourceObjectID = @SourceObjectID END ELSE BEGIN UPDATE meta.FrameworkMetaData SET PartitionSQLScript = @DefaultPartitionSQL WHERE SourceObjectID = @SourceObjectID END END ELSE BEGIN IF @PartitionFlag = 1 AND @UseModulusFlag = 0 BEGIN PRINT(@SQL) END ELSE BEGIN PRINT(@DefaultPartitionSQL) END END
SET @PlaceholderSQL = ''
SET @SQL = ''
SET @Counter = 1
DELETE FROM @InformationSchema
SET @PartitionFlag = NULL SET @SourceObjectID = NULL SET @UseModulusFlag = NULL
SET @OuterCounter = @OuterCounter + 1
END
SET NOCOUNT OFF

/**********************************************************************************************************************************************************************
The purpose of this scripts is to create the create table script for extract tables
***********************************************************************************************************************************************************************/

CREATE PROCEDURE [meta].[MaintainExtractCreateTable] 

@SourceObjectID INT,--Input is the table name without schema
@DropTable BIT, --Input is 1 if you want the table to be dropped if it already exists
@PrintSQL BIT

AS

SET NOCOUNT ON
/********************************************************************************************************************************************************************** Support variables ***********************************************************************************************************************************************************************/ DECLARE @CRLF NVARCHAR(2) = CHAR(13) + CHAR(10) DECLARE @ConnectionType NVARCHAR(128) = (SELECT ConnectionType FROM [meta].[SourceObjectDefinitions] WHERE SourceObjectID = @SourceObjectID) DECLARE @FileName NVARCHAR(128) = (SELECT FileName FROM [meta].[SourceObjectDefinitions] WHERE SourceObjectID = @SourceObjectID)
/********************************************************************************************************************************************************************** 1. Update ExtractInformationSchema table **********************************************************************************************************************************************************************/
DELETE FROM meta.ExtractInformationSchema WHERE SourceObjectID= @SourceObjectID;
INSERT INTO meta.ExtractInformationSchema ( [SourceObjectID], [SourceSystemTypeName], [TableCatalogName], [SchemaName], [TableName], [ColumnName], [OrdinalPositionNumber], [DataTypeName], [MaximumLenghtNumber], [NumericPrecisionNumber], [NumericScaleNumber], [KeySequenceNumber] )
SELECT @SourceObjectID AS SourceObjectID ,@ConnectionType AS SourceSystemTypeName ,null AS TableCatalogName ,null AS SchemaName ,@FileName AS TableName ,SourceItems.name AS ColumnName ,ROW_NUMBER() OVER (PARTITION BY SourceHeader.itemName, SourceHeader.itemType ORDER BY (SELECT NULL)) AS OrdinalPositionNumber ,SourceItems.type AS DataTypeName ,null AS MaximumLenghtNumber ,null AS NumericPrecisionNumber ,null AS NumericScaleNumber ,null AS KeySequenceNumber FROM OPENJSON(@JsonOutput) WITH( itemName varchar(200), itemType varchar(200), structure nvarchar(max) '$.structure' AS JSON ) SourceHeader CROSS APPLY OPENJSON(SourceHeader.structure) WITH(name nvarchar(200) ,type nvarchar(200)) SourceItems
SET NOCOUNT OFF

/**********************************************************************************************************************************************************************
The purpose of this scripts is to update meta data information schema table based on Get Metadata activity in ADF
***********************************************************************************************************************************************************************/

CREATE PROCEDURE [meta].[MaintainExtractInformationSchemaFromJsonMeta] 
@SourceObjectID int, -- SourceObjectID
@JsonOutput NVARCHAR(MAX)--Json output from ADF Get Meta Data activity

AS

SET NOCOUNT ON
/********************************************************************************************************************************************************************** Support variables ***********************************************************************************************************************************************************************/ DECLARE @CRLF NVARCHAR(2) = CHAR(13) + CHAR(10) DECLARE @DatabaseNameExtract NVARCHAR(128) = (SELECT VariableValue FROM meta.Variables WHERE VariableName = 'DatabaseNameExtract')
/********************************************************************************************************************************************************************** 1. Update FrameworkMetaData table with IDs ***********************************************************************************************************************************************************************/ INSERT INTO meta.FrameworkMetaData (TargetObjectID)
SELECT TargetObjects.ID FROM meta.TargetObjects LEFT JOIN meta.FrameworkMetaData ON FrameworkMetaData.TargetObjectID = TargetObjects.ID WHERE FrameworkMetaData.BusinessMatrixID IS NULL AND FrameworkMetaData.SourceObjectID IS NULL AND FrameworkMetaData.TargetObjectID IS NULL AND TargetObjects.ID = @TargetObjectID
DELETE FROM meta.FrameworkMetaData WHERE TargetObjectID NOT IN (SELECT ID FROM meta.TargetObjects) AND TargetObjectID IS NOT NULL;
/********************************************************************************************************************************************************************** 2. Create and insert data into table variables **********************************************************************************************************************************************************************/ DECLARE @InformationSchemaTables TABLE (TargetObjectID INT, FileSystemName NVARCHAR(128),TargetConnectionName NVARCHAR(128), FolderName NVARCHAR(128), FileFormat NVARCHAR(128), AzureDWSchemaName NVARCHAR(128), OrdinalPosition INT) INSERT @InformationSchemaTables SELECT TargetObjectID,TargetFileSystemName, TargetConnectionName, TargetFolderName, TargetAzureFileTypeName, TargetExtractSchemaName,ROW_NUMBER() OVER ( ORDER BY TargetObjectID) FROM meta.TargetObjectDefinitions WHERE TargetObjectID = @TargetObjectID AND TargetAzureSqlDWFlag = 1 GROUP BY TargetObjectID,TargetFileSystemName,TargetConnectionName,TargetFolderName,TargetAzureFileTypeName,TargetExtractSchemaName
/********************************************************************************************************************************************************************** 3. Create Loop counter variables **********************************************************************************************************************************************************************/ DECLARE @Counter INT = 1 DECLARE @MaxTable INT = (SELECT MAX(OrdinalPosition) FROM @InformationSchemaTables)
/********************************************************************************************************************************************************************** 4. Create outer loop **********************************************************************************************************************************************************************/ DECLARE @SQL NVARCHAR(MAX)
WHILE @Counter <= @MaxTable
BEGIN
SELECT @TargetObjectID = TargetObjectID ,@SQL = 'IF (SELECT [name] FROM [sys].[external_data_sources] WHERE [name] = ''' + TargetConnectionName + FileSystemName + ''') IS NULL' + @CRLF + 'CREATE EXTERNAL DATA SOURCE ' + TargetConnectionName + FileSystemName + ' WITH ( LOCATION=''abfss://' + LOWER(FileSystemName) + '@' + LOWER(TargetConnectionName) + '.dfs.core.windows.net'', CREDENTIAL = ' + TargetConnectionName + ', TYPE = HADOOP ); IF (SELECT [name] FROM [sys].[schemas] WHERE [name] = ''' + AzureDWSchemaName + ''') IS NULL' + @CRLF + ' EXEC(''CREATE SCHEMA ' + AzureDWSchemaName + ' AUTHORIZATION dbo''); IF (SELECT [name] FROM [sys].[external_file_formats] WHERE [name] = ''' + FileFormat + 'Format'') IS NULL' + @CRLF + ' EXEC(''' + CASE FileFormat WHEN 'DelimitedText' THEN 'CREATE EXTERNAL FILE FORMAT ' + FileFormat + 'Format WITH (FORMAT_TYPE = DELIMITEDTEXT, FORMAT_OPTIONS(FIELD_TERMINATOR = '''','''',STRING_DELIMITER = ''''\"'''',USE_TYPE_DEFAULT = True))' WHEN 'Parquet' THEN 'CREATE EXTERNAL FILE FORMAT ' + FileFormat + 'Format WITH (FORMAT_TYPE = PARQUET, DATA_COMPRESSION = ''''org.apache.hadoop.io.compress.SnappyCodec'''')' WHEN 'Orc' THEN 'CREATE EXTERNAL FILE FORMAT ' + FileFormat + 'Format WITH (FORMAT_TYPE = PARQUET, DATA_COMPRESSION = ''''org.apache.hadoop.io.compress.SnappyCodec'''')' ELSE '' END + ''')'
FROM @InformationSchemaTables WHERE @Counter = OrdinalPosition
/********************************************************************************************************************************************************************** 8. Update FrameworkMetaDate **********************************************************************************************************************************************************************/ IF @PrintSQL = 0 BEGIN UPDATE FrameworkMetaData SET ConnectionSQLScript = @SQL FROM meta.FrameworkMetaData WHERE TargetObjectID = @TargetObjectID END ELSE BEGIN PRINT(@SQL) END
SET @SQL = '' SET @Counter = @Counter + 1
END
SET NOCOUNT OFF

