Property.Value:
-- Drop the table if it already exists IF OBJECT_ID('dim.Time', 'U') IS NOT NULL BEGIN DROP TABLE dim.Time; END
-- Then create a new table CREATE TABLE dim.[Time]( [TimeID] [int] IDENTITY(1,1) NOT NULL, [TimeHourKey] [int] NULL, [TimeMinuteKey] [int] NULL, [Time] [time](0) NULL, [TimeDayPart] [nvarchar](10) NULL, [TimeHourFromTo] [nvarchar](13) NULL, [TimeNotation] [nvarchar](10) NULL );
-- Needed if the dimension already existed -- with other column, otherwise the validation -- of the insert could fail.
-- Create a time and a counter variable for the loop DECLARE @Time as time; SET @Time = '0:00';
DECLARE @counter as int; SET @counter = 0;
-- Two variables to store the day part for two languages DECLARE @daypartEN as varchar(20); set @daypartEN = '';
-- Loop 1440 times (24hours * 60minutes) WHILE @counter < 1440 BEGIN
-- Determine datepart SELECT @daypartEN = CASE WHEN (@Time >= '0:00' and @Time < '6:00') THEN 'Night' WHEN (@Time >= '6:00' and @Time < '12:00') THEN 'Morning' WHEN (@Time >= '12:00' and @Time < '18:00') THEN 'Afternoon' ELSE 'Evening' END;
INSERT INTO dim.Time ( [TimeHourKey] ,[TimeMinuteKey] ,[Time] ,[TimeDayPart] ,[TimeHourFromTo] ,[TimeNotation] ) VALUES ( DATEPART(Hour, @Time) + 1 , DATEPART(Minute, @Time) + 1 , @Time , @daypartEN , CAST(DATEADD(Minute, -DATEPART(Minute,@Time), @Time) as varchar(5)) + ' - ' + CAST(DATEADD(Hour, 1, DATEADD(Minute, -DATEPART(Minute,@Time), @Time)) as varchar(5)) , CAST(@Time as varchar(5)) );
-- Raise time with one minute SET @Time = DATEADD(minute, 1, @Time);
-- Raise counter by one set @counter = @counter + 1; END
EXEC('Create View dimView.Time AS SELECT * FROM dim.Time')

BEGIN
	DECLARE
		@a INT
		,@b INT
		,@c INT
		,@d INT
		,@e INT
		,@f INT
		,@g INT
		,@h INT
		,@i INT
		,@k INT
		,@l INT
		,@m INT
		,@n INT
		,@p INT
		,@eastersunday DATE

	SET @a = @year % 19;
	SET @b = @year / 100;
	SET @c = @year % 100;
	SET @d = @b / 4;
	SET @e = @b % 4;
	SET @f = (@b + 8) / 25;
	SET @g = (@b - @f + 1) / 3;
	SET @h = ((19 * @a) + @b - @d - @g + 15) % 30;
	SET @i = @c / 4;
	SET @k = @c % 4;
	SET @l = (32 + (2 * @e) + (2 * @i) - @h -@k) % 7;
	SET @m = (@a + (11 * @h) + (22 * @l)) / 451;
	SET @n = (@h + @l - (7 * @m) + 114) / 31;
	SET @p = ((@h + @l - (7 * @m) + 114) % 31) + 1;

	SET @eastersunday = DATEFROMPARTS(@year,@n,@p);

    RETURN @eastersunday
END

