AS

CREATE PROCEDURE [meta].[UpdateVariables]

@DatabasePrefix NVARCHAR(100),
@DatabaseNameDW NVARCHAR(100),
@DatabaseNameStage NVARCHAR(100),
@DatabaseNameExtract NVARCHAR(100),
@DatabaseNameMeta NVARCHAR(100),
@IsCloudFlag BIT

AS

SET NOCOUNT ON
--/* --DECLARE @Table NVARCHAR(128) = 'Fact_CustomerProducts' --*/
DROP TABLE IF EXISTS #OnlyFactColumns DROP TABLE IF EXISTS #MapFactsAndDimensions DROP TABLE IF EXISTS #FactColumns DROP TABLE IF EXISTS #BaseAndRolePlayingDimensions DROP TABLE IF EXISTS #RolePlayingDimensions DROP TABLE IF EXISTS #BaseDimensions DROP TABLE IF EXISTS #SCD2Dimensions
DECLARE @DatabaseNameDW NVARCHAR(128) = (SELECT VariableValue FROM nuuMetaView.Variables WHERE VariableName = 'DatabaseNameDW') DECLARE @DatabaseNameStage NVARCHAR(128) = (SELECT VariableValue FROM nuuMetaView.Variables WHERE VariableName = 'DatabaseNameStage') DECLARE @BusinessKeySuffix NVARCHAR(10) = (SELECT VariableValue FROM nuuMetaView.Variables WHERE VariableName = 'BusinessKeySuffix')
/*1. Create a dataset with all base dimensions--*/ SELECT DISTINCT TABLE_NAME AS BaseDimensionName, COLUMN_NAME AS BaseDimensionColumnName --The case statement removes the dimension name from the column in order to identity composite keys where part of the key has it own dimension , CASE WHEN REPLACE( COLUMN_NAME, TABLE_NAME, '' ) = '' + @BusinessKeySuffix + '' THEN COLUMN_NAME ELSE REPLACE( COLUMN_NAME, TABLE_NAME, '' ) END AS DimensionCompositeKeyColumnName --The last column is the column name of that comes from the fact. It only differs on the calendar dimension , COLUMN_NAME AS ColumnNameFromFact INTO #BaseDimensions FROM INFORMATION_SCHEMA.COLUMNS WHERE COLUMN_NAME LIKE '%' + @BusinessKeySuffix + '' AND TABLE_SCHEMA = 'dim'
/*2. Create a dataset with all fact columns*/ SELECT TABLE_NAME AS FactName, COLUMN_NAME AS FactColumnName INTO #FactColumns FROM INFORMATION_SCHEMA.COLUMNS WHERE COLUMN_NAME LIKE '%' + @BusinessKeySuffix + '' AND TABLE_NAME = '' + @Table + '' AND TABLE_SCHEMA = 'stage'
/*3. Create a dataset with all role playing dimensions*/ SELECT DISTINCT FactColumns.FactName ,FactColumns.FactColumnName ,MatchBaseAndRolePlaying.BaseDimensionName ,MatchBaseAndRolePlaying.DimensionCompositeKeyColumnName ,REPLACE( FactColumns.FactColumnName, REPLACE( MatchBaseAndRolePlaying.DimensionCompositeKeyColumnName, MatchBaseAndRolePlaying.BaseDimensionName, '' ), '' ) AS RolePlayingDimensionName --Since the rows are multiplied when joining role playing dimensions with base dimensions a row number is added to identify unique role playing dimensions ,ROW_NUMBER() OVER ( PARTITION BY MatchBaseAndRolePlaying.BaseDimensionName ORDER BY LEN(MatchBaseAndRolePlaying.BaseDimensionColumnName) ) AS RowNumberUniqueRolePlayingDimensions --Since the rows are multiplied when joining role playing dimensions with base dimensions a row number is added to identify unique role playing dimension keys ,ROW_NUMBER() OVER ( PARTITION BY FactColumns.FactColumnName ORDER BY CHARINDEX(REPLACE(MatchBaseAndRolePlaying.DimensionCompositeKeyColumnName,'' + @BusinessKeySuffix + '',''), FactColumns.FactColumnName)
--Substracting the number of characters from RolePlayingDimensionName columns in order to secure that only rows which holds the exact name of the roleplaying dimensions has RowN = 1 - LEN(REPLACE( FactColumns.FactColumnName, REPLACE( MatchBaseAndRolePlaying.DimensionCompositeKeyColumnName, MatchBaseAndRolePlaying.BaseDimensionName, '' ), '' ) ) DESC ) AS RowNumberRolePlayingDimensionsKeys INTO #RolePlayingDimensions FROM #FactColumns AS FactColumns --The first join is used for removing base dimensions from the list of columns LEFT JOIN #BaseDimensions AS BaseDimensions ON FactColumns.FactColumnName = BaseDimensions.ColumnNameFromFact --The second join matches the role playing dimensions with the base dimensions INNER JOIN #BaseDimensions AS MatchBaseAndRolePlaying ON FactColumns.FactColumnName LIKE MatchBaseAndRolePlaying.BaseDimensionName + '%' WHERE BaseDimensions.DimensionCompositeKeyColumnName IS NULL
/*4. Create a dataset with SCD2 dimensions*/ SELECT DISTINCT tables.name AS SCD2DimensionName INTO #SCD2Dimensions FROM sys.tables INNER JOIN sys.all_columns ON all_columns.object_id=tables.object_id INNER JOIN sys.extended_properties ON extended_properties.major_id=tables.object_id AND extended_properties.minor_id=all_columns.column_id AND extended_properties.class=1 WHERE extended_properties.name = 'HistoryType' AND extended_properties.value = 'Type2'
/*5. Create a dataset with all dimensions including role playing dimensions*/ --Base Dimensions SELECT DimensionCompositeKeyColumnName, BaseDimensionColumnName, BaseDimensionName, BaseDimensionName AS BaseAndRolePlayingDimensionName INTO #BaseAndRolePlayingDimensions FROM #BaseDimensions AS BaseDimensions
UNION
-- Role Playing Dimensioner SELECT CASE -- If the columnname minus the dimensionname exist in the fact the base dimension key is used WHEN KeyIsPartOfOtherDimension.BaseDimensionName IS NOT NULL THEN REPLACE( BaseDimensions.BaseDimensionColumnName, RolePlayingDimensions.BaseDimensionName, '' ) -- If it is the calendar dimension the column from the fact is used ELSE CASE WHEN RolePlayingDimensions.BaseDimensionName = 'Calendar' THEN FactColumnName -- Else the correct name is created from name of the role playing dimension and the base dimension key columns ELSE CONCAT( RolePlayingDimensions.RolePlayingDimensionName, REPLACE( BaseDimensions.BaseDimensionColumnName, RolePlayingDimensions.BaseDimensionName, '' ) ) END END AS DimensionCompositeKeyColumnName, BaseDimensions.BaseDimensionColumnName, RolePlayingDimensions.BaseDimensionName, RolePlayingDimensions.RolePlayingDimensionName AS BaseAndRolePlayingDimensionName FROM #RolePlayingDimensions AS RolePlayingDimensions -- Der joines med basis dimensionerne for at f? r?kkerne eksploderet ud og f? de korrekte n?glekolonner INNER JOIN #BaseDimensions AS BaseDimensions ON RolePlayingDimensions.BaseDimensionName = BaseDimensions.BaseDimensionName LEFT JOIN #BaseDimensions AS KeyIsPartOfOtherDimension ON REPLACE( BaseDimensions.BaseDimensionColumnName, RolePlayingDimensions.BaseDimensionName, '' ) = KeyIsPartOfOtherDimension.BaseDimensionColumnName -- Skal kun bruge de unikke Role Playing dimensioner WHERE RolePlayingDimensions.RowNumberRolePlayingDimensionsKeys = 1
/*6. Create a dataset which maps fact/bridge and dimensions.*/ SELECT DISTINCT FactColumns.FactName, BaseAndRolePlayingDimensions.BaseDimensionName, CASE WHEN FactColumns.FactColumnName = BaseAndRolePlayingDimensions.BaseDimensionColumnName THEN BaseAndRolePlayingDimensions.BaseDimensionColumnName ELSE BaseAndRolePlayingDimensions.DimensionCompositeKeyColumnName END AS FactColumnName, BaseAndRolePlayingDimensions.BaseDimensionColumnName, ISNULL( RolePlayingDimensions.RolePlayingDimensionName, BaseDimensions.BaseDimensionName ) AS BaseAndRolePlayingDimensionName INTO #MapFactsAndDimensions FROM #FactColumns AS FactColumns --First step is to match fact columns with base dimension columns. This gives us the dimension name which is used when mapping to BaseAndRolePlayingDimensions LEFT JOIN #BaseDimensions AS BaseDimensions ON FactColumns.FactColumnName = BaseDimensions.ColumnNameFromFact --Second step is to match the role playing dimensions to get the role playing dimension name LEFT JOIN #RolePlayingDimensions AS RolePlayingDimensions ON RolePlayingDimensions.FactColumnName = FactColumns.FactColumnName AND RowNumberRolePlayingDimensionsKeys = 1 --Third step is to match with the combined list of dimensions LEFT JOIN #BaseAndRolePlayingDimensions AS BaseAndRolePlayingDimensions ON ISNULL( RolePlayingDimensions.RolePlayingDimensionName, BaseDimensions.BaseDimensionName ) = BaseAndRolePlayingDimensions.BaseAndRolePlayingDimensionName WHERE BaseAndRolePlayingDimensions.BaseDimensionName IS NOT NULL
/*7. Create the final dataset which filter out incorrect mappings and make the final transformations.*/ SELECT MapFactsAndDimensions.FactName, MapFactsAndDimensions.BaseDimensionName, MapFactsAndDimensions.FactColumnName, MapFactsAndDimensions.BaseDimensionColumnName, MapFactsAndDimensions.BaseAndRolePlayingDimensionName --This case indicates whether a dimension is a SCD2 dimension , CASE WHEN MapFactsAndDimensions.BaseDimensionName IN (SELECT SCD2DimensionName FROM #SCD2Dimensions) THEN N'Yes' ELSE N'No' END AS IsSCD2DimensionFlag --This case indicates whether a column is part of a composite key and primary key in its own dimension which is a SCD2 dimension. Information is used in etl.LoadFact , CASE WHEN REPLACE( MapFactsAndDimensions.FactColumnName, '' + @BusinessKeySuffix + '', '' ) IN (SELECT SCD2DimensionName FROM #SCD2Dimensions) THEN N'Yes' ELSE N'No' END AS IsSCD2CompositeKeyDimensionFlag --Creates a ordinal position with the correct ordering , ROW_NUMBER() OVER ( PARTITION BY MapFactsAndDimensions.FactName ORDER BY MapFactsAndDimensions.BaseAndRolePlayingDimensionName, MapFactsAndDimensions.BaseDimensionColumnName ) AS ColumnOrdinalPosition --This case indicates the when a row goes from one dimension to the next. , CASE WHEN ROW_NUMBER() OVER ( PARTITION BY MapFactsAndDimensions.BaseAndRolePlayingDimensionName ORDER BY MapFactsAndDimensions.BaseAndRolePlayingDimensionName, MapFactsAndDimensions.BaseDimensionColumnName ) = 1 THEN N'Yes' ELSE N'No' END AS IsNewDimensionFlag --This case adds the default error value to the dataset , CASE WHEN MapFactsAndDimensions.BaseDimensionName = 'Calendar' THEN ( SELECT CONVERT( NVARCHAR(128), value ) FROM sys.extended_properties WHERE class_desc = 'DATABASE' AND name = 'DefaultDate' ) ELSE ( SELECT CONVERT( NVARCHAR(128), value ) FROM sys.extended_properties WHERE class_desc = 'DATABASE' AND name = 'DefaultDimensionMemberID' ) END AS DefaultErrorValue INTO #OnlyFactColumns FROM #MapFactsAndDimensions AS MapFactsAndDimensions --The last join filter out incorrect mappings INNER JOIN #FactColumns AS FactColumns ON FactColumns.FactColumnName = MapFactsAndDimensions.FactColumnName
/*8.Remove mappings where only part of composite key is mapped to fact like CompanyKey*/ SELECT FactName, BaseDimensionName, FactColumnName, BaseDimensionColumnName, BaseAndRolePlayingDimensionName, IsSCD2DimensionFlag, IsSCD2CompositeKeyDimensionFlag, ROW_NUMBER() OVER (PARTITION BY FactName ORDER BY IsSCD2DimensionFlag, ColumnOrdinalPosition) AS ColumnOrdinalPosition, IsNewDimensionFlag, DefaultErrorValue FROM #OnlyFactColumns WHERE BaseDimensionName IN ( SELECT DISTINCT BaseDimensionName FROM #OnlyFactColumns WHERE BaseDimensionColumnName LIKE BaseDimensionName + '%' )
SET NOCOUNT OFF

BEGIN
	DECLARE
		@a INT
		,@b INT
		,@c INT
		,@d INT
		,@e INT
		,@f INT
		,@g INT
		,@h INT
		,@i INT
		,@k INT
		,@l INT
		,@m INT
		,@n INT
		,@p INT
		,@eastersunday DATE

	SET @a = @year % 19;
	SET @b = @year / 100;
	SET @c = @year % 100;
	SET @d = @b / 4;
	SET @e = @b % 4;
	SET @f = (@b + 8) / 25;
	SET @g = (@b - @f + 1) / 3;
	SET @h = ((19 * @a) + @b - @d - @g + 15) % 30;
	SET @i = @c / 4;
	SET @k = @c % 4;
	SET @l = (32 + (2 * @e) + (2 * @i) - @h -@k) % 7;
	SET @m = (@a + (11 * @h) + (22 * @l)) / 451;
	SET @n = (@h + @l - (7 * @m) + 114) / 31;
	SET @p = ((@h + @l - (7 * @m) + 114) % 31) + 1;

	SET @eastersunday = DATEFROMPARTS(@year,@n,@p);

    RETURN @eastersunday
END

CREATE PROCEDURE [nuuMeta].[GetLastValueLoaded]
@SourceConnectionName nvarchar(200) , @SourceSchemaName nvarchar(200) , @SourceTableName nvarchar(200) , @JobIsIncremental BIT , @ConnectionType NVARCHAR(50) , @WatermarkIsDate BIT
AS

/**********************************************************************************************************************************************************************
The purpose of this scripts is to create and execute the merge join statement between stage tables and dimensions. 
***********************************************************************************************************************************************************************/

CREATE PROCEDURE [nuuMeta].[LoadDimension]
	@StageTable  NVARCHAR(100), --Input is the stage table name without schema
	@DWTable  NVARCHAR(100), --Input is the dimension table name without schema
	@PrintSQL BIT = 0
AS

/* DECLARE @StageTable NVARCHAR(100) = 'Fact_CustomerProducts', --Input is the dimensions name without schema @DWSchema NVARCHAR(10) = 'fact', @DWTable NVARCHAR(100) = 'CustomerProducts', --Input is the dimensions name without schema @LoadPattern NVARCHAR(50) = 'FactMerge', @IncrementalFlag BIT = 1, @CleanUpPartitionsFlag BIT = 1, @PrintSQL BIT = 1 --*/
SET NOCOUNT ON
/********************************************************************************************************************************************************************** Support variables ***********************************************************************************************************************************************************************/
DECLARE @CRLF NVARCHAR(2) = CHAR(13) + CHAR(10) DECLARE @DatabaseNameStage NVARCHAR(128) = (SELECT VariableValue FROM nuuMetaView.Variables WHERE VariableName = 'DatabaseNameStage') DECLARE @DatabaseNameMeta NVARCHAR(128) = (SELECT VariableValue FROM nuuMetaView.Variables WHERE VariableName = 'DatabaseNameMeta') DECLARE @DatabaseNameDW NVARCHAR(128) = (SELECT VariableValue FROM nuuMetaView.Variables WHERE VariableName = 'DatabaseNameDW') DECLARE @SurrogateKeySuffix NVARCHAR(10) = (SELECT VariableValue FROM nuuMetaView.Variables WHERE VariableName = 'SurrogateKeySuffix') DECLARE @BusinessKeySuffix NVARCHAR(10) = (SELECT VariableValue FROM nuuMetaView.Variables WHERE VariableName = 'BusinessKeySuffix') DECLARE @IncrementalFact BIT = @IncrementalFlag DECLARE @IsCloudFlag BIT = 1 DECLARE @StageSchema NVARCHAR(10) = 'stage' DECLARE @MaxCalendarKey NVARCHAR(10) = (SELECT MAX(CalendarKey) FROM dim.Calendar)
/********************************************************************************************************************************************************************** 1. Create and insert data into table variables ***********************************************************************************************************************************************************************/ DROP TABLE IF EXISTS #InformationSchema DROP TABLE IF EXISTS #Dimensions DROP TABLE IF EXISTS #Type2CombinedKeys DROP TABLE IF EXISTS #Type2FromSource
CREATE TABLE #InformationSchema (DatabaseName NVARCHAR(128),TableName NVARCHAR(128), ColumnName NVARCHAR(128), OrdinalPosition INT, DataType NVARCHAR(128), PrimaryKey INT) CREATE TABLE #Dimensions (TableName NVARCHAR(128), DimensionTable NVARCHAR(128), ColumnName NVARCHAR(128),ColumnMapping NVARCHAR(128),RolePlayingDimension NVARCHAR(128),IsType2Dimension NVARCHAR(10), IsType2CompositeKeyDimension NVARCHAR(10),OrdinalPosition INT,NewDimension NVARCHAR(128), ErrorValue NVARCHAR(128)) CREATE TABLE #Type2CombinedKeys (DimensionTable NVARCHAR(128), ColumnName NVARCHAR(128), ColumnMapping NVARCHAR(128),OrdinalPosition INT, RolePlayingDimension NVARCHAR(128),NewDimension NVARCHAR(128), ErrorValue NVARCHAR(128)) CREATE TABLE #Type2FromSource (DimensionTable NVARCHAR(128))
/*Generates the combined information schema*/
INSERT INTO #InformationSchema ( DatabaseName, TableName, ColumnName, OrdinalPosition, DataType, PrimaryKey ) SELECT 'Stage' AS DatabaseName, COLUMNS.TABLE_NAME AS TableName, COLUMNS.COLUMN_NAME AS ColumnName, COLUMNS.ORDINAL_POSITION, COLUMNS.DATA_TYPE AS DataType, CASE WHEN COLUMN_NAME LIKE '%Unique' THEN 1 ELSE 0 END AS PrimaryKey FROM INFORMATION_SCHEMA.COLUMNS WHERE COLUMNS.TABLE_NAME = @StageTable AND COLUMNS.COLUMN_NAME NOT LIKE 'DW%' AND COLUMNS.TABLE_SCHEMA = @StageSchema
/*Generates the mapping dataset between fact/bridge and dimensions*/ INSERT INTO #Dimensions (TableName,DimensionTable,ColumnName,ColumnMapping,RolePlayingDimension,IsType2Dimension,IsType2CompositeKeyDimension,OrdinalPosition,NewDimension,ErrorValue) EXEC nuuMeta.CreateDWRelations @Table = @StageTable
DROP TABLE IF EXISTS #DW SELECT 'DW' AS DatabaseName, inf.TableName, ISNULL( dim.RolePlayingDimension + 'ID', inf.ColumnName ) AS ColumnName, LEAD( dim.RolePlayingDimension ) OVER (PARTITION BY dim.RolePlayingDimension ORDER BY inf.ColumnName) AS Lead, ROW_NUMBER() OVER (ORDER BY inf.OrdinalPosition) AS OrdinalPosition, IIF( dim.RolePlayingDimension IS NOT NULL, 'int', inf.DataType ) AS DataType, inf.PrimaryKey INTO #DW FROM #InformationSchema AS inf LEFT JOIN #Dimensions AS dim ON dim.ColumnName = inf.ColumnName GROUP BY inf.DatabaseName, inf.TableName, dim.RolePlayingDimension, inf.ColumnName, inf.OrdinalPosition, inf.DataType, inf.PrimaryKey
INSERT INTO #InformationSchema (DatabaseName,TableName,ColumnName,OrdinalPosition,DataType,PrimaryKey) SELECT DatabaseName, TableName, ColumnName, ROW_NUMBER() OVER (ORDER BY OrdinalPosition), DataType, PrimaryKey FROM #DW WHERE lead IS NULL
/*Generates a dataset with composite Type2 keys*/ INSERT INTO #Type2CombinedKeys (DimensionTable,ColumnName,ColumnMapping,OrdinalPosition,RolePlayingDimension,NewDimension,ErrorValue) SELECT dim.DimensionTable, inf.ColumnName, dim.ColumnMapping, ROW_NUMBER() OVER (ORDER BY CASE WHEN dim.RolePlayingDimension IS NULL THEN dim.DimensionTable ELSE dim.RolePlayingDimension END, inf.OrdinalPosition) AS ORDINAL_POSITION, CASE WHEN dim.RolePlayingDimension IS NULL THEN dim.DimensionTable ELSE dim.RolePlayingDimension END, CASE WHEN ROW_NUMBER() OVER (PARTITION BY CASE WHEN dim.RolePlayingDimension IS NULL THEN dim.DimensionTable ELSE dim.RolePlayingDimension END ORDER BY dim.DimensionTable, inf.OrdinalPosition) = 1 THEN N'Yes' ELSE N'No' END, dim.ErrorValue FROM #InformationSchema AS inf LEFT JOIN #Dimensions AS dim ON dim.ColumnName = inf.ColumnName WHERE dim.IsType2Dimension = 'Yes' AND inf.DatabaseName = 'Stage'
/********************************************************************************************************************************************************************** 2. Create Loop counter variables and Type2FromSource variable ***********************************************************************************************************************************************************************/
DECLARE @Counter INT DECLARE @MaxColumns INT --Number of columns from stage DECLARE @MaxType2JoinColumns INT --Max position of composite Type2 key columns DECLARE @MaxJoinColumns INT --Max position of key columns from fact/bridge DECLARE @MaxColumnsKeys INT --Max position of primary key columns i fact/bridge DECLARE @MaxColumnsFact INT --Number of columns in fact/bridge
/********************************************************************************************************************************************************************** 3. Create position and support variables ***********************************************************************************************************************************************************************/ DECLARE @HasCalendarKey INT DECLARE @HasTimeKey INT
SELECT @HasCalendarKey = CASE WHEN (SELECT COUNT(*) FROM #InformationSchema WHERE ColumnName = 'Calendar' + @BusinessKeySuffix) > 0 THEN 1 ELSE 0 END --Check if CalendarKey is present ,@HasTimeKey = CASE WHEN (SELECT COUNT(*) FROM #InformationSchema WHERE ColumnName = 'TimeHour' + @BusinessKeySuffix) > 0 THEN 1 ELSE 0 END --Check if TimeKey is present
/********************************************************************************************************************************************************************** 5. Create the select part of the source code for non ID columns ***********************************************************************************************************************************************************************/ DECLARE @Select NVARCHAR(MAX) = ''
SELECT @Select = STRING_AGG('[' + inf.TableName + '].[' + inf.ColumnName + ']',',') FROM #InformationSchema AS inf WHERE inf.DatabaseName = 'Stage' AND inf.ColumnName NOT LIKE '%'+@BusinessKeySuffix
SET @Select = @Select + ','
/********************************************************************************************************************************************************************** 6. Create the select Type2 ID with error handling ***********************************************************************************************************************************************************************/ DECLARE @IDType2Merge NVARCHAR(MAX) DECLARE @IDType2Columns NVARCHAR(MAX) DECLARE @IDType2 NVARCHAR(MAX)
SELECT @IDType2Merge = STRING_AGG('ISNULL([' + RolePlayingDimension + '].[' + DimensionTable + @SurrogateKeySuffix + '],' + ErrorValue +') AS [' + RolePlayingDimension + @SurrogateKeySuffix + ']',',') , @IDType2Columns = STRING_AGG('[' + RolePlayingDimension + @SurrogateKeySuffix + ']',',') , @IDType2 = STRING_AGG('[' + RolePlayingDimension + '].[' + DimensionTable + @SurrogateKeySuffix + ']' + CASE WHEN RolePlayingDimension <> DimensionTable THEN ' AS [' + RolePlayingDimension + @SurrogateKeySuffix + ']' ELSE '' END,',') FROM #Type2CombinedKeys WHERE NewDimension = 'Yes'
SET @IDType2Merge = @IDType2Merge + ',' SET @IDType2Columns = @IDType2Columns + ',' SET @IDType2 = @IDType2 + ','
/********************************************************************************************************************************************************************** 7. Create the select ID part for source code ***********************************************************************************************************************************************************************/ DECLARE @IDMerge NVARCHAR(MAX)
SELECT @IDMerge = STRING_AGG( CASE WHEN RolePlayingDimension IS NOT NULL THEN 'ISNULL([' + RolePlayingDimension + '].[' + DimensionTable + @SurrogateKeySuffix + '],' + ErrorValue + ') AS [' + RolePlayingDimension + @SurrogateKeySuffix + ']' ELSE 'ISNULL([' + DimensionTable + '].[' + DimensionTable + @SurrogateKeySuffix + '],' + ErrorValue + ') AS ' + '[' + DimensionTable + @SurrogateKeySuffix + ']' END, ',') FROM #Dimensions WHERE NewDimension = 'Yes' AND IsType2Dimension = 'No'
SET @IDMerge = @IDMerge + ','
/********************************************************************************************************************************************************************** 8. Create the the left join part for Type2 dimensions ***********************************************************************************************************************************************************************/ DECLARE @LeftJoinType2 NVARCHAR(MAX) DECLARE @DatetimeValue NVARCHAR(MAX)
/* DatetimeValue is set dependent on if there is a timehour key or not. If there is we convert calendarkey and time keys into a datetime. The replicate function makes sure to add a leading 0 if we are at single digit number */ SET @DatetimeValue = CASE WHEN @HasTimeKey=1 THEN 'Convert(datetime, STUFF(STUFF(CONCAT(REPLACE([' + @StageTable + '].[CalendarKey], ''-'', ''''),REPLICATE(''0'',2 -LEN(RTRIM([' + @StageTable + '].[TimeHourKey]))) + RTRIM([' + @StageTable + '].[TimeHourKey]),REPLICATE(''0'',2 -LEN(RTRIM([' + @StageTable + '].[TimeMinuteKey]))) + RTRIM([' + @StageTable + '].[TimeMinuteKey])),11,0,'':''),9,0,'' ''))' ELSE 'Convert(date, CONCAT([' + @StageTable + '].[CalendarKey], '' '',''00:00:00''))' END
SELECT @LeftJoinType2 = STRING_AGG( CASE WHEN NewDimension = 'Yes' THEN 'LEFT JOIN [' + @DatabaseNameDW + '].[dim].[' + Type2CombinedKeys.DimensionTable + '] ' + CASE WHEN RolePlayingDimension IS NOT NULL THEN ' AS [' + RolePlayingDimension + ']' ELSE '' END + @CRLF + 'ON ' + CASE WHEN @HasCalendarKey = 1 AND Type2FromSource.DimensionTable IS NULL THEN @DatetimeValue + ' >= [' + RolePlayingDimension + '].[DWValidFromDate] AND ' + @DatetimeValue + ' < [' + RolePlayingDimension + '].[DWValidToDate]' WHEN @HasCalendarKey = 1 AND Type2FromSource.DimensionTable IS NOT NULL THEN @DatetimeValue + ' >= [' + RolePlayingDimension + '].[' + Type2FromSource.DimensionTable + 'ValidFromDate] AND ' + @DatetimeValue + ' < [' + RolePlayingDimension + '].[' + Type2FromSource.DimensionTable + 'ValidToDate]' WHEN @HasCalendarKey = 0 AND Type2FromSource.DimensionTable IS NOT NULL THEN '[' + RolePlayingDimension + '].[' + Type2FromSource.DimensionTable + 'IsCurrent] = 1 ' ELSE '[' + RolePlayingDimension + '].[DWIsCurrent] = 1 ' END + @CRLF + 'AND [' + RolePlayingDimension + '].[' + ColumnMapping + '] = [' + @StageTable + '].[' + ColumnName + ']' ELSE 'AND [' + RolePlayingDimension + '].[' + ColumnMapping + '] = [' + @StageTable + '].[' + ColumnName + ']' END, @CRLF) FROM #Type2CombinedKeys AS Type2CombinedKeys LEFT JOIN #Type2FromSource AS Type2FromSource ON Type2FromSource.DimensionTable = Type2CombinedKeys.DimensionTable
/********************************************************************************************************************************************************************** 9. Create the left join part for non Type2 dimensions ***********************************************************************************************************************************************************************/ DECLARE @LeftJoin NVARCHAR(MAX)
SELECT @LeftJoin = STRING_AGG( CASE WHEN NewDimension = 'Yes' THEN 'LEFT JOIN [' + @DatabaseNameDW + '].[dim].[' + DimensionTable + '] ' + CASE WHEN RolePlayingDimension IS NOT NULL THEN ' AS [' + RolePlayingDimension + ']' ELSE '' END + @CRLF + 'ON [' + CASE WHEN RolePlayingDimension IS NOT NULL THEN RolePlayingDimension ELSE DimensionTable END + '].[' + ColumnMapping + '] =' +
CASE WHEN ColumnMapping = 'CalendarKey' THEN ' IIF([' + @StageTable + '].[' + ColumnName + '] > ''' + @MaxCalendarKey + ''',''' + @MaxCalendarKey + ''',[' + @StageTable + '].[' + ColumnName + '] )' ELSE '[' + @StageTable + '].[' + ColumnName + ']' END
ELSE 'AND [' + CASE WHEN RolePlayingDimension IS NOT NULL THEN RolePlayingDimension ELSE DimensionTable END + '].[' + ColumnMapping + '] = ' +
CASE WHEN ColumnMapping = 'CalendarKey' THEN ' IIF([' + @StageTable + '].[' + ColumnName + '] > ''' + @MaxCalendarKey + ''',''' + @MaxCalendarKey + ''',[' + @StageTable + '].[' + ColumnName + '] )' ELSE '[' + @StageTable + '].[' + ColumnName + ']' END END, @CRLF) FROM #Dimensions WHERE IsType2Dimension = 'No'
/********************************************************************************************************************************************************************** 10. Create the key part of the merge join statement ***********************************************************************************************************************************************************************/
DECLARE @Keys NVARCHAR(MAX) --Holds the value of @Keys
SELECT @Keys = STRING_AGG('[source].[' + ColumnName + '] = [target].[' + ColumnName + ']',' AND ') FROM #InformationSchema WHERE PrimaryKey = 1 AND DatabaseName = 'DW'
/********************************************************************************************************************************************************************** 11. Create the columns from stage for the merge script ***********************************************************************************************************************************************************************/ DECLARE @ColumnNameFact NVARCHAR(MAX) --Holds the value of @ColumnNameDim for each loop
SELECT @ColumnNameFact = STRING_AGG('[' + InformationSchema.ColumnName + ']',',') FROM #InformationSchema AS InformationSchema WHERE DatabaseName = 'DW' AND InformationSchema.ColumnName NOT LIKE '%ID'
SET @ColumnNameFact = @ColumnNameFact + ','
/********************************************************************************************************************************************************************** 11. Create the columns from stage for the merge script ***********************************************************************************************************************************************************************/ DECLARE @ColumnNameIDFact NVARCHAR(MAX) --Holds the value of @ColumnNameDim for each loop
SELECT @ColumnNameIDFact = STRING_AGG('[' + InformationSchema.ColumnName + ']',',') FROM #InformationSchema AS InformationSchema LEFT JOIN (SELECT RolePlayingDimension, SUM( OrdinalPosition ) AS OrdinalPosition FROM #Type2CombinedKeys GROUP BY RolePlayingDimension ) AS Type2Columns ON CONCAT(Type2Columns.RolePlayingDimension,'ID') = InformationSchema.ColumnName WHERE DatabaseName = 'DW' AND Type2Columns.RolePlayingDimension IS NULL AND InformationSchema.ColumnName LIKE '%ID'
SET @ColumnNameIDFact = @ColumnNameIDFact + ','
/********************************************************************************************************************************************************************** 12. Create the match part and the update part for the merge script ***********************************************************************************************************************************************************************/ DECLARE @UpdateType21 NVARCHAR(MAX) --Holds the value of @ColumnNameDim for each loop DECLARE @MatchType21 NVARCHAR(MAX) --Holds the value of @ColumnNameDim for each loop
SELECT @MatchType21 = STRING_AGG('([target].[' + ColumnName + '] <> [source].[' + ColumnName + ']) OR ([target].[' + ColumnName + '] IS NULL AND [source].[' + ColumnName + '] IS NOT NULL) OR ([target].[' + ColumnName + '] IS NOT NULL AND [source].[' + ColumnName + '] IS NULL)', ','), @UpdateType21 = STRING_AGG('[target].[' + ColumnName + '] = [source].[' + ColumnName + ']',',') FROM #InformationSchema WHERE DatabaseName = 'DW'
/********************************************************************************************************************************************************************** 13. Create the delta part of the source code ***********************************************************************************************************************************************************************/ DECLARE @SelectDelta NVARCHAR(MAX) = ''
SELECT @SelectDelta = STRING_AGG( CASE WHEN ColumnName NOT LIKE '%ID' AND ColumnName NOT LIKE '%Code' AND ColumnName NOT LIKE '%Number' AND Datatype IN ('Decimal','Numeric','int','bigint') THEN '-' ELSE '' END + '[target].[' + InformationSchema.ColumnName + ']' ,',') FROM #InformationSchema AS InformationSchema WHERE InformationSchema.DatabaseName = 'DW' AND InformationSchema.ColumnName <> 'LastValueLoaded'
/********************************************************************************************************************************************************************** 14. Fill out dynamic SQL variables ***********************************************************************************************************************************************************************/
DECLARE @DeleteFromFact NVARCHAR(MAX) --Holds the Type21 part of the Merge Join Script DECLARE @SQLFullLoad NVARCHAR(MAX) --Holds the Type21 part of the Merge Join Script DECLARE @SQLDelta NVARCHAR(MAX) DECLARE @SQLReplacement NVARCHAR(MAX) DECLARE @SQLStandard NVARCHAR(MAX) DECLARE @SQLAdd NVARCHAR(MAX) DECLARE @SQLMergeJoin NVARCHAR(MAX) --Holds the Type21 part of the Merge Join Script DECLARE @SQL NVARCHAR(MAX) --Holds the Type21 part of the Merge Join Script
SET @SQLMergeJoin =' DECLARE @CurrentDateTime datetime DECLARE @MinDateTime datetime DECLARE @MaxDateTime datetime DECLARE @BooleanTrue bit DECLARE @BooleanFalse bit DECLARE @DateToDateTime datetime
SELECT @CurrentDateTime = cast(getdate() as datetime), @MinDateTime = cast(''1900-01-01'' as datetime), @MaxDateTime = cast(''9999-12-31'' as datetime), @BooleanTrue = cast(1 as bit), @BooleanFalse = cast(0 as bit), @DateToDateTime = dateadd(ms,-3, getdate())
-- ================================================== -- Type21 -- ==================================================
MERGE [' + @DatabaseNameDW + '].[' + @DWSchema + '].['+ @DWTable + '] as [target] USING [' + @DatabaseNameDW + '].[' + @DWSchema + '].[' + @DWTable + '_Temp] as [source]
-- Selects source rows in order to compare them to [target]
ON ( ' + @Keys + ' )
WHEN NOT MATCHED BY TARGET THEN
INSERT ( ' + ISNULL(@IDType2Columns,'') + + ISNULL(@ColumnNameIDFact,'') + + ISNULL(@ColumnNameFact,'') + ' [DWCreatedDate] ,[DWModifiedDate] ) VALUES (' + ISNULL(@IDType2Columns,'') + + ISNULL(@ColumnNameIDFact,'') + + ISNULL(@ColumnNameFact,'') + ' @CurrentDateTime ,@CurrentDateTime )
WHEN MATCHED
THEN UPDATE
SET '+ @UpdateType21 + ', [target].[DWModifiedDate] = @CurrentDateTime
;'
SET @SQLFullLoad = 'TRUNCATE TABLE [' + @DatabaseNameDW + '].[' + @DWSchema + '].['+ @DWTable + ']
EXEC [' + @DatabaseNameMeta + '].nuuMeta.[MaintainDWFactIndexes] @DWTable = ''' + @DWTable + ''', @DWSchema = ''' + @DWSchema + ''',@DisableIndexes = 1, @PrintSQL = 0
DECLARE @UpdateDateTime datetime = GETDATE()
INSERT INTO [' + @DatabaseNameDW + '].[' + @DWSchema + '].['+ @DWTable + '] WITH (TABLOCK) ( ' + ISNULL(@IDType2Columns,'') + ISNULL(@ColumnNameIDFact,'') + ISNULL(@ColumnNameFact,'') + ' DWCreatedDate ,DWModifiedDate )
SELECT ' + ISNULL(@IDType2Merge,'') + ISNULL(@IDMerge,'') + ISNULL(@Select,'') + ' @UpdateDateTime ,@UpdateDateTime FROM [' + @DatabaseNameStage + '].[' + @StageSchema + '].['+ @StageTable + '] AS ' + @StageTable + ' ' + @CRLF + ISNULL(@LeftJoinType2,'') + ISNULL(@LeftJoin,'') + @CRLF + @CRLF +
'EXEC [' + @DatabaseNameMeta + '].nuuMeta.[MaintainDWFactIndexes] @DWTable = ''' + @DWTable + ''', @DWSchema = ''' + @DWSchema + ''', @DisableIndexes = 0, @PrintSQL = 0'
SET @SQLStandard = 'TRUNCATE TABLE [' + @DatabaseNameDW + '].[' + @DWSchema + '].['+ @DWTable + '_Temp]
DECLARE @UpdateDateTime datetime = GETDATE()
INSERT INTO [' + @DatabaseNameDW + '].[' + @DWSchema + '].['+ @DWTable + '_Temp] WITH (TABLOCK) ( ' + ISNULL(@IDType2Columns,'') + + ISNULL(@ColumnNameIDFact,'') + ISNULL(@ColumnNameFact,'') + ' DWCreatedDate ,DWModifiedDate )
SELECT ' + ISNULL(@IDType2Merge,'') + ISNULL(@IDMerge,'') + ISNULL(@Select ,'') + ' @UpdateDateTime AS DWCreatedDate , @UpdateDateTime AS DWModifiedDate FROM [' + @DatabaseNameStage + '].[' + @StageSchema + '].['+ @StageTable + '] AS ' + @StageTable + ' ' + @CRLF + ISNULL(@LeftJoinType2,'') + ISNULL(@LeftJoin,'') + @CRLF + @CRLF
SET @SQLAdd = 'TRUNCATE TABLE [' + @DatabaseNameDW + '].[' + @DWSchema + '].['+ @DWTable + '_Temp]
BEGIN TRY BEGIN TRAN
DECLARE @UpdateDateTime datetime = GETDATE()
INSERT INTO [' + @DatabaseNameDW + '].[' + @DWSchema + '].['+ @DWTable + '] WITH (TABLOCK) ( ' + ISNULL(@IDType2Columns,'') + + ISNULL(@ColumnNameIDFact,'') + ISNULL(@ColumnNameFact,'') + ' DWCreatedDate ,DWModifiedDate )
SELECT ' + ISNULL(@IDType2Merge,'') + ISNULL(@IDMerge,'') + ISNULL(@Select ,'') + '@UpdateDateTime' + @CRLF + ', @UpdateDateTime FROM [' + @DatabaseNameStage + '].[' + @StageSchema + '].['+ @StageTable + '] AS ' + @StageTable + ' ' + ISNULL(@LeftJoinType2,'') + ISNULL(@LeftJoin,'') +
IIF(@CleanUpPartitionsFlag = 0,'
INSERT INTO [' + @DatabaseNameDW + '].[' + @DWSchema + '].['+ @DWTable + '_Temp] WITH (TABLOCK) ( ' + ISNULL(@IDType2Columns,'') + + ISNULL(@ColumnNameIDFact,'') + ISNULL(@ColumnNameFact,'') + ' DWCreatedDate ,DWModifiedDate )
SELECT ' + ISNULL(@IDType2Merge,'') + ISNULL(@IDMerge,'') + ISNULL(@Select ,'') + '@UpdateDateTime AS DWCreatedDate ' + @CRLF + ', @UpdateDateTime AS DWModifiedDate FROM [' + @DatabaseNameStage + '].[' + @StageSchema + '].['+ @StageTable + '] AS ' + @StageTable + ' ' + ISNULL(@LeftJoinType2,'') + ISNULL(@LeftJoin,''),'') + ' COMMIT TRAN
END TRY BEGIN CATCH IF @@TRANCOUNT > 0 ROLLBACK TRAN; --RollBack in case of Error THROW; END CATCH'
------------------------------------------------------- -- @DeleteFromFact ------------------------------------------------------- SET @DeleteFromFact = ' DELETE [target] WITH (TABLOCK) FROM [' + @DatabaseNameDW + '].[' + @DWSchema + '].['+ @DWTable + '] AS [target]' + @CRLF + 'INNER JOIN [' + @DatabaseNameStage + '].[' + @StageSchema + '].['+ @DWTable + '] AS [source]' + @CRLF + 'ON ' + + @Keys
------------------------------------------------------- -- @SQLDelta ------------------------------------------------------- SET @SQLDelta = 'TRUNCATE TABLE [' + @DatabaseNameDW + '].[' + @DWSchema + '].['+ @DWTable + '_Temp] ' + @CRLF + @CRLF +
'BEGIN TRY BEGIN TRAN' + @CRLF + @CRLF +
IIF(@CleanUpPartitionsFlag = 0,' EXEC [' + @DatabaseNameMeta + '].nuuMeta.FactPatternsDelta @DWTable = ''' + @DWTable + ''', @DWSchema = ''' + @DWSchema + ''', @CleanUpPartitionsFlag = 0, @PrintSQL = 0','') + @CRLF + IIF(@CleanUpPartitionsFlag = 1,@DeleteFromFact,'') + @CRLF + @CRLF +
' DECLARE @UpdateDateTime datetime = GETDATE()
INSERT INTO [' + @DatabaseNameDW + '].[' + @DWSchema + '].['+ @DWTable + '] WITH (TABLOCK) ( ' + ISNULL(@IDType2Columns,'') + + ISNULL(@ColumnNameIDFact,'') + ISNULL(@ColumnNameFact,'') + ' DWCreatedDate ,DWModifiedDate )
SELECT ' + ISNULL(@IDType2Merge,'') + ISNULL(@IDMerge,'') + ISNULL(@Select ,'') + '@UpdateDateTime' + @CRLF + ', @UpdateDateTime FROM [' + @DatabaseNameStage + '].[' + @StageSchema + '].['+ @StageTable + '] AS ' + @StageTable + ' ' + @CRLF + ISNULL(@LeftJoinType2,'') + ISNULL(@LeftJoin,'') + @CRLF + @CRLF +
IIF(@CleanUpPartitionsFlag = 0, 'INSERT INTO [' + @DatabaseNameDW + '].[' + @DWSchema + '].['+ @DWTable + '_Temp] WITH (TABLOCK) ( ' + ISNULL(@IDType2Columns,'') + + ISNULL(@ColumnNameIDFact,'') + ISNULL(@ColumnNameFact,'') + ' DWCreatedDate ,DWModifiedDate )
SELECT ' + ISNULL(@IDType2Merge,'') + ISNULL(@IDMerge,'') + ISNULL(@Select ,'') + '@UpdateDateTime AS DWCreatedDate ' + @CRLF + ', @UpdateDateTime AS DWModifiedDate FROM [' + @DatabaseNameStage + '].[' + @StageSchema + '].['+ @StageTable + '] AS ' + @StageTable + ' ' + @CRLF + ISNULL(@LeftJoinType2,'') + ISNULL(@LeftJoin,'') ,'') + @CRLF + @CRLF +
'COMMIT TRAN END TRY BEGIN CATCH IF @@TRANCOUNT > 0 ROLLBACK TRAN; --RollBack in case of Error THROW; END CATCH'
------------------------------------------------------- -- @SQLReplacement -------------------------------------------------------
SET @SQLReplacement = 'TRUNCATE TABLE [' + @DatabaseNameDW + '].[' + @DWSchema + '].['+ @DWTable + '_Temp] ' + @CRLF + @CRLF +
'BEGIN TRY BEGIN TRAN' + @CRLF + @CRLF +
IIF(@CleanUpPartitionsFlag = 0,' EXEC [' + @DatabaseNameMeta + '].nuuMeta.FactPatternsReplacement @DWTable = ''' + @DWTable + ''', @DWSchema = ''' + @DWSchema + ''', @CleanUpPartitionsFlag = 0, @PrintSQL = 0','') + @CRLF + IIF(@CleanUpPartitionsFlag = 1,' EXEC [' + @DatabaseNameMeta + '].nuuMeta.FactPatternsReplacement @DWTable = ''' + @DWTable + ''', @DWSchema = ''' + @DWSchema + ''', @CleanUpPartitionsFlag = 1, @PrintSQL = 0','') + @CRLF + @CRLF +
'DECLARE @UpdateDateTime datetime = GETDATE()
INSERT INTO [' + @DatabaseNameDW + '].[' + @DWSchema + '].['+ @DWTable + '] WITH (TABLOCK) ( ' + ISNULL(@IDType2Columns,'') + + ISNULL(@ColumnNameIDFact,'') + ISNULL(@ColumnNameFact,'') + ' DWCreatedDate ,DWModifiedDate )
SELECT ' + ISNULL(@IDType2Merge,'') + ISNULL(@IDMerge,'') + ISNULL(@Select ,'') + '@UpdateDateTime' + @CRLF + ', @UpdateDateTime FROM [' + @DatabaseNameStage + '].[' + @StageSchema + '].['+ @StageTable + '] AS ' + @StageTable + ' ' + @CRLF + ISNULL(@LeftJoinType2,'') + ISNULL(@LeftJoin,'') + @CRLF + @CRLF +
IIF(@CleanUpPartitionsFlag = 0, 'INSERT INTO [' + @DatabaseNameDW + '].[' + @DWSchema + '].['+ @DWTable + '_Temp] WITH (TABLOCK) ( ' + ISNULL(@IDType2Columns,'') + + ISNULL(@ColumnNameIDFact,'') + ISNULL(@ColumnNameFact,'') + ' DWCreatedDate ,DWModifiedDate )
SELECT ' + ISNULL(@IDType2Merge,'') + ISNULL(@IDMerge,'') + ISNULL(@Select ,'') + '@UpdateDateTime AS DWCreatedDate ' + @CRLF + ', @UpdateDateTime AS DWModifiedDate FROM [' + @DatabaseNameStage + '].[' + @StageSchema + '].['+ @StageTable + '] AS ' + @StageTable + ' ' + @CRLF + ISNULL(@LeftJoinType2,'') + ISNULL(@LeftJoin,'') ,'') + @CRLF + @CRLF +
'COMMIT TRAN END TRY BEGIN CATCH IF @@TRANCOUNT > 0 ROLLBACK TRAN; --RollBack in case of Error THROW; END CATCH'
SET @SQL = CASE WHEN @IncrementalFact = 0 THEN @SQLFullLoad WHEN @IncrementalFact = 1 AND @LoadPattern = 'FactMerge' THEN CONCAT(@SQLStandard,@SQLMergeJoin) WHEN @IncrementalFact = 1 AND @LoadPattern = 'FactAdd' THEN @SQLAdd WHEN @IncrementalFact = 1 AND @LoadPattern = 'FactDelta' THEN @SQLDelta WHEN @IncrementalFact = 1 AND @LoadPattern = 'FactReplacement' THEN @SQLReplacement END
IF @PrintSQL = 0 BEGIN
EXEC(@SQL)
END ELSE BEGIN
SELECT CAST('<?SQL --' +@CRLF +ISNULL(@SQL,'') +@CRLF + '-- ?>' AS XML) SQLScript
END

